(8, 15, 'admin', 'demo@demo.org', '', '192.168.9.78', '2015-05-25 11:51:21', '2015-05-25 11:51:21', 'Just got my new position. The discussed matter is really urgent for me.', 0, '1', '', '', 0, 0),
(9, 15, 'admin', 'demo@demo.org', '', '192.168.9.78', '2015-05-25 11:51:50', '2015-05-25 11:51:50', 'I wish I’ve read this article earlier… Useful tips for all young managers.', 0, '1', '', '', 0, 0),
(10, 15, 'admin', 'demo@demo.org', '', '192.168.9.78', '2015-05-25 11:52:16', '2015-05-25 11:52:16', 'Thanks for sharing! Will try to avoid the listed mistakes.', 0, '1', '', '', 8, 0),
